import React, { Fragment, useState } from 'react';

const InputTodo = () => {

    const [descrriptions, setDescriptions] = useState("");

    const onSubmitForm = async() => {
        try {
            const body = {descrriptions};
            const response = await fetch("http://localhost:5300/todo", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body)
            });

            console.log(response);

        } catch (err) {
            console.error(err.message);

        }
    }



    return (
        <Fragment>
            <h1 className="text-center mt-5">Pern Todo List</h1>
            <form className="d-flex mt-5" onSubmit={onSubmitForm}>
                <input type="text" className="form-control" value={descrriptions} onChange={e => setDescriptions(e.target.value)}/>
                <button className="btn btn-success">Add</button>
            </form>
        </Fragment>
    );
};

export default InputTodo;
